import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { DataGrid } from '@mui/x-data-grid';
import CircularProgress from '@mui/material/CircularProgress';
import Box from '@mui/material/Box';

// Define schema fields dynamically for rendering
const contactSchema = [
  { field: 'firstName', headerName: 'First Name', width: 150 },
  { field: 'lastName', headerName: 'Last Name', width: 150 },
  { field: 'email', headerName: 'Email', width: 250 },
  { field: 'phoneNumber', headerName: 'Phone Number', width: 180 },
  { field: 'company', headerName: 'Company', width: 200 },
  { field: 'jobTitle', headerName: 'Job Title', width: 180 },
];

const ContactsTable = () => {
  const [contacts, setContacts] = useState([]);
  const [loading, setLoading] = useState(true);

  // Fetch contacts from the API
  useEffect(() => {
    const fetchContacts = async () => {
      try {
        const response = await axios.get('http://localhost:4000/contacts');
        // Adding an 'id' field required by DataGrid
        const contactsWithId = response.data.map((contact, index) => ({
          ...contact,
          id: contact._id || index,
        }));
        setContacts(contactsWithId);
      } catch (error) {
        console.error('Error fetching contacts:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchContacts();
  }, []);

  if (loading) {
    return (
      <Box display="flex" justifyContent="center" marginTop="20px">
        <CircularProgress />
      </Box>
    );
  }

  return (
    <Box sx={{ height: 500, width: '100%' }}>
      <DataGrid
        rows={contacts}
        columns={contactSchema}
        pageSize={10}
        rowsPerPageOptions={[5, 10, 20]}
        checkboxSelection
        disableSelectionOnClick
        sortingOrder={['asc', 'desc']}
        autoHeight
      />
    </Box>
  );
};

export default ContactsTable;
